#define global_html "<html><head><link rel=\"stylesheet\" href=\"/styles\"/><script>function globalData(method, callback, body){ var xmlHttp=new XMLHttpRequest(); xmlHttp.onload=function(){ if (callback !==null){ if (xmlHttp.readyState==4 && xmlHttp.status==200){ callback(xmlHttp.responseText);} else{ callback(null);}}}; xmlHttp.open(method, \"/global-data\", true); xmlHttp.send(body);} function fillData(text){ var data={}; if (text !==null){ var keyValuesList=text.split(\";\"); for (var i=0; i < keyValuesList.length; i++){var keyValue=keyValuesList[i].split(\":\"); data[keyValue[0]]=keyValue[1]}} document.getElementById(\"ssid\").value=(data.SSID) ? data.SSID : \"\"; document.getElementById(\"password\").value=(data.PSWD) ? data.PSWD : \"\"; document.getElementById(\"adm_pass\").value=(data.ADM) ? data.ADM : \"\";} function saveData(){ var body=\"\"; body +=\"SSID:\" + document.getElementById(\"ssid\").value + \";\"; body +=\"PSWD:\" + document.getElementById(\"password\").value + \";\"; body +=\"ADM:\" + document.getElementById(\"adm_pass\").value + \";\"; globalData('POST', null, body);} </script></head><body onload=\"globalData('GET', fillData, null)\"><h1 class=\"white\">Synestesia</h1><div><form><div><label class=\"white\">SSID</label><input type=\"text\" class=\"form-control\" id=\"ssid\" placeholder=\"SSID\"></div><div><label class=\"white\">Password</label><input type=\"text\" class=\"form-control\" id=\"password\" placeholder=\"Password\"></div><div><label class=\"white\">Admin Password</label><input type=\"password\" class=\"form-control\" id=\"adm_pass\" placeholder=\"Password\"></div></form></div><div><button type=\"button\" onclick=\"saveData()\">Save</button></div></body></html>"