#define styles_css ".white{ font-family: Verdana; color: white} .center{ display: flex; justify-content: center; align-items: center} body{ width: 100%;padding-top: 5%; margin: auto; max-width: 500px; animation: bg-color 10s infinite} h1{font-size: 100} label{font-size: 50} button{ background-color:white;border-radius: 20px; width: 120px; height: 70px; border-style: none; display: block; margin: auto; max-width: 360px; max-height: 100px; min-width: 100px;min-height: 40px} input{ height: 5%; width: 70%; max-width: 360px; max-height: 100px; border-radius: 15px; text-align: center; border-style: none; display: block; margin: auto; font-size: 30;} @keyframes bg-color{ 0%{background-color: #2ecc71} 20%{background-color:#3498db} 40%{background-color:#9b59b6} 60%{background-color:#e74c3c} 80%{background-color: #f5ef42} 100%{background-color: #2ecc71}}"